CREATE PROCEDURE [dbo].[InsertBooking]
    @UserID VARCHAR(36),
    @MovieID VARCHAR(36),
    @TheatreID VARCHAR(36),
    @BookingDate DATETIME,
    @ShowDate DATE,
    @ShowTime VARCHAR(10),
    @ScreenNumber INT,
    @NumberOfSeats INT,
    @Seats VARCHAR(MAX),
    @TotalPrice DECIMAL(10, 2),
    @NewBookingID VARCHAR(36) OUTPUT
AS
BEGIN
    -- Declare a table variable to capture the output
    DECLARE @InsertedIDs TABLE (BookingID VARCHAR(36));
    
    -- Insert the new booking and capture the BookingID
    INSERT INTO Bookings (UserID, MovieID, TheatreID, BookingDate, ShowDate, ShowTime, ScreenNumber, NumberOfSeats, Seats, TotalPrice)
    OUTPUT Inserted.BookingID INTO @InsertedIDs
    VALUES (@UserID, @MovieID, @TheatreID, @BookingDate, @ShowDate, @ShowTime, @ScreenNumber, @NumberOfSeats, @Seats, @TotalPrice);
    
    -- Retrieve the BookingID from the table variable and set the output parameter
    SELECT @NewBookingID = BookingID FROM @InsertedIDs;
END;
GO
