CREATE PROCEDURE [dbo].[InsertUser]
    @FullName VARCHAR(50),
    @PasswordHash VARCHAR(255),
    @Email VARCHAR(100),
    @PhoneNumber VARCHAR(15),
    @SecurityQuestion VARCHAR(200),
    @SecurityAnswer VARCHAR(100),
	@NewUserID VARCHAR(36) OUTPUT
AS
BEGIN
	 DECLARE @InsertedIDs TABLE (UserID VARCHAR(36));

    INSERT INTO Users (FullName, PasswordHash, Email, PhoneNumber, SecurityQuestion, SecurityAnswer)
    OUTPUT Inserted.UserID INTO @InsertedIDs
	VALUES (@FullName, @PasswordHash, @Email, @PhoneNumber, @SecurityQuestion, @SecurityAnswer);
    
    SELECT @NewUserID = UserID FROM @InsertedIDs;
END;
